import "./widgetSm.css";
import { DataGrid } from "@material-ui/data-grid";
import { DeleteOutline } from "@material-ui/icons";
import { userRows } from "../../dummyData";
import { Link } from "react-router-dom";
import { useState } from "react";

export default function WidgetSm() {
  const [data, setData] = useState(userRows);

  const handleDelete = (id) => {
    setData(data.filter((item) => item.id !== id));
  };

  const columns = [
    { field: "id", headerName: "ID", width: 90 },
    { field: "suite_id", headerName: "Suite #", width: 120 },
    {
      field: "user",
      headerName: "User",
      width: 160,
      renderCell: (params) => {
        return (
          <div className="userListUser">
            <Link to={"/userOrders/" + params.row.id} className="userLinkOrders">
            <img className="userListImg" src={params.row.avatar} alt="" />
            {params.row.username}
            </Link>
          </div>
        );
      },
    },
    { field: "email", headerName: "Email", width: 120 },
    {
      field: "premium_member",
      headerName: "Premium",
      width: 140,
    },
    {
      field: "processing",
      headerName: "Proc.",
      width: 110,
    },
    {
      field: "recived",
      headerName: "In",
      width: 90,
    },
    {
      field: "shipped",
      headerName: "Out",
      width: 100,
    },
    {
      field: "transaction",
      headerName: "Total Transactions",
      width: 150,
    },
    {
      field: "latest_transaction",
      headerName: "Latest Transaction",
      width: 150,
    },
    {
      field: "signedUp_date",
      headerName: "Signed Up",
      width: 150,
    },   
    {
      field: "action",
      headerName: "Action",
      width: 150,
      renderCell: (params) => {
        return (
          <>
            <Link to={"/user/" + params.row.id}>
              <button className="userListEdit">Edit</button>
            </Link>
            <DeleteOutline
              className="userListDelete"
              onClick={() => handleDelete(params.row.id)}
            />
          </>
        );
      },
    },
  ];

  return (

    <div className="widgetSm">
            <div className="widgetSmTitle">
        <h2 className="userTitle">Users</h2>
        <Link to="/newUser">
          <button className="userAddButton">Create</button>
        </Link>
        </div>
      <DataGrid
        rows={data}
        disableSelectionOnClick
        columns={columns}
        pageSize={4}
        checkboxSelection
      />
    </div>
  );

}
